            xx =[0:360-1]; %
            yy =abs(beta(1)).*exp(abs(beta(2)).*cos(xx./180*pi))+abs(beta(1)).*exp(abs(beta(2)).*cos((xx-180)./180*pi))+abs(beta(3)).*exp(abs(beta(4)).*cos((xx-(RaOri(i)-0)*2)./180*pi))+abs(beta(5));
